package Lesson2;

public class WhileStatement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a =3;
		
		while(a>4)
		{
			
		System.out.println(a);	
			a++;
		}
		
		System.out.println("-------------------------------");
		
		do{
			
			System.out.println(a);	
			
		}while (a>4);
	}

}
